import Homepage from './page/homepage/Homepage';
import TopBar from './components/topbar/TopBar';
import Single from './page/single/Single';
import Write from './page/write/Write';
import Settings from './page/settings/Settings';
import Login from './page/login/Login';
import Register from './page/register/Register';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Routes,
} from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from './context/AuthContext';

function App() {
  const { user } = useContext(AuthContext);
  return (
    <Router>
      <TopBar />
      <Routes>
        <Route path="/" element={<Homepage  />} />
        <Route path="/register">{user ?  element=<Homepage  />: element=<Register />}</Route>
        <Route path="/login">{user ? element=<Homepage  /> : element=<Login />}</Route>
        <Route path="/write">{user ? element=<Write /> : element=<Login />}</Route>
        <Route path="/settings">{user ? element=<Settings /> : element=<Register />}</Route>
        <Route path="/post/:postId"
         element={<Single />} />
      </Routes>
    </Router>
  );
}

export default App;
